pageContext.rootQuery:
query BradGridQuery($slug: String!, $skip: Int!, $limit: Int!) {
  wordpressPage(slug: { eq: $slug }) {
    title
    slug
    content
    template
  }

  allBradDailies: allWordpressWpDailyReads(
    limit: $limit,
    skip: $skip,
  ) {
    edges {
      node {
        id
        slug
        title
        content
      }
    }
  }
}
