pageContext.rootQuery:
query ClientAppQuery($slug: String!) {
  wordpressPage(slug: { eq: $slug }) {
    title
    slug
    content
    template
    acf {
      app_screenshot {
        localFile {
          childImageSharp {
            fluid(maxWidth: 600){
              ...GatsbyImageSharpFluid
            }
          }
        }
      }
      app_features {
        icon {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
        description
        title
      }
    }
  }
}
