pageContext.rootQuery:
query EventsGridQuery($slug: String!, $skip: Int, $limit: Int) {
  wordpressPage(slug: { eq: $slug }) {
    title
    slug
    content
    template
  }

  allEvents: allWordpressWpEvents(
    limit: $limit,
    skip: $skip,
  ) {
    edges {
      node {
        title
        slug
        link
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
        acf {
          event_description
          event_time
          event_address
          event_date
        }
      }
    }
  }
}
