pageContext.rootQuery:
query PostsGridQuery($slug: String!, $skip: Int, $limit: Int, $filter: String) {
  wordpressPage(slug: { eq: $slug }) {
    title
    slug
    content
    template
  }
  allWordpressPost(
    limit: $limit,
    skip: $skip,
    filter: {
      title: {
        glob: $filter
      }
    }
  ) {
    edges {
      node {
        id
        slug
        title
        content
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
      }
    }
  }

  allPosts: allWordpressPost(filter: {
    categories: {
      elemMatch: {
        slug:{
          eq: $slug
        }
      }
    }
  }) {
    edges {
      node {
        id
        slug
        title
        content
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
      }
    }
  }
}
