pageContext.rootQuery:
query allPageData {
  allPosts: allWordpressPost {
    edges {
      node {
        id
        slug
        title
        content
        excerpt
        categories {
          name
        }
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
      }
    }
  }
  allPages: allWordpressPage {
    edges {
      node {
        id
        slug
        title
        template
        acf {
          content_blocks_page {
            __typename
            ... on WordPressAcf_intro_section {
              intro_text
            }
            __typename
            ... on WordPressAcf_regular_content {
              content_text
            }
            __typename
            ... on WordPressAcf_two_columns {
              two_columns {
                column_left
                column_right
              }
            }
            __typename
            ... on WordPressAcf_three_columns {
              three_columns_block {
                column_left
                column_middle
                column_right
              }
            }
            __typename
            ... on WordPressAcf_four_columns {
              four_columns_block {
                column_left
                column_middle_left
                column_middle_right
                column_right
              }
            }
            __typename
            ... on WordPressAcf_featured_content {
              featured_content_block {
                content
              }
            }
          }
        }
      }
    }
  }
  allEvents: allWordpressWpEvents {
    edges {
      node{
        id
        title
        slug
        acf {
          event_address
          event_description
        }
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 400){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
      }
    }
  }
  allDailyReads: allWordpressWpDailyReads {
    edges {
      node {
        slug
        title
        content
      }
    }
  }
}
