pageContext.rootQuery:
query ServicesQuery($slug: String!) {
  wordpressWpServices(slug: { eq: $slug }) {
    excerpt
    slug
    title
    wordpress_id
    acf {
      work_with_us
      content_blocks_services{
        __typename
        ... on WordPressAcf_intro_section {
          intro_text
          id
        }
        __typename
        ... on WordPressAcf_regular_content {
          content_text
          id
        }
        __typename
        ... on WordPressAcf_two_columns {
          two_columns {
            column_left
            column_right
          }
          id
        }
        __typename
        ... on WordPressAcf_three_columns {
          three_columns_block {
            column_left
            column_middle
            column_right
          }
          id
        }
        __typename
        ... on WordPressAcf_four_columns {
          four_columns_block {
            column_left
            column_middle_left
            column_middle_right
            column_right
          }
          id
        }
        __typename
        ... on WordPressAcf_featured_content {
          featured_content_block {
            content
            image {
              source_url
              id
            }
          }
          id
        }
      }
    }
  }
  wordpressWpApiMenusMenusItems(slug: {eq: "main-menu"}) {
    items {
      classes
      title
      object_id
      url
      object_slug
      order
      wordpress_children {
        wordpress_parent
        classes
        object_id
        object_slug
        title
        type
        type_label
      }
    }
  }
}
