pageContext.rootQuery:
query AboutPageQuery($slug: String!) {
  wordpressPage(slug: { eq: $slug }) {
    title
    slug
    content
    template
    acf {
      hero {
        text
        image {
          localFile {
            childImageSharp {
              fluid(maxWidth: 1200) {
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
      }
      team_members_grid {
        wordpress_id
        post_excerpt
        post_title
        post_status
        post_name
      }
    }
  }
  allWordpressWpTeam {
    edges {
      node {
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 800){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
        title
        wordpress_id
        acf {
          title
          team_member_bio
        }
      }
    }
  }
}
