pageContext.rootQuery:
query WhoWeServeQuery($slug: String!) {
  wordpressPage(slug: {eq: $slug }) {
    title
    slug
    content
    template
    acf {
      client_personas_grid {
        post_title
        post_status
        wordpress_id
      }
      content_blocks_page{
        __typename
        ...RegularContentBlock
      }
    }
  }
  allWordpressWpPersona {
    edges {
      node {
        excerpt
        featured_media {
          localFile {
            childImageSharp {
              fluid(maxWidth: 800){
                ...GatsbyImageSharpFluid
              }
            }
          }
        }
        slug
        title
        wordpress_id
        acf {
          persona_description
          work_with_us
        }
      }
    }
  }
}
